mapper.update:
UPDATE cmreply
SET content = #{content}, regdate = sysdate, ismod = '1'
WHERE rno = #{rno}

mapper.select:

SELECT * FROM	
		(
		SELECT ROWNUM AS rn, tbl.* FROM
			(
			SELECT * FROM cmreply
			ORDER BY rno DESC
			) tbl
		)
		WHERE bno = #{bno}

  			SELECT * FROM	
			(
			SELECT ROWNUM AS rn, tbl.* FROM
				(
				SELECT * FROM cmreply

					WHERE writer LIKE '%'||#{keyword}||'%'

				ORDER BY rno DESC
				) tbl
			)

		WHERE rn > (#{pageNum} - 1) * #{countPerPage}
		AND rn <= #{pageNum} * #{countPerPage}
