mapper.update:
UPDATE noboard
SET title = #{title}, content = #{content}, regdate = sysdate, ismod = '1'
WHERE bno = #{bno}

mapper.select:
  			SELECT * FROM	
			(
			SELECT ROWNUM AS rn, tbl.* FROM
				(
				SELECT * FROM noboard
				ORDER BY bno DESC
				) tbl
			)

		WHERE rn > (#{pageNum} - 1) * #{countPerPage}
		AND rn <= #{pageNum} * #{countPerPage}
