mapper.select:
SELECT * FROM ( SELECT ROWNUM as rn, tbl.* FROM ( SELECT pr.*, u.filenum FROM promoboard pr LEFT JOIN users u ON pr.writer = u.username
ORDER BY bno DESC ) tbl )
WHERE rn > (#{pageNum} - 1) * #{countPerPage} AND rn <= #{pageNum} * #{countPerPage}

SELECT COUNT(*) FROM
promoboardlike 
WHERE bno = #{bno}
